import React, { useState } from "react";
import axios from "axios";

function App() {
  const [identifier, setIdentifier] = useState("");
  const [songUri, setSongUri] = useState("");
  const [joined, setJoined] = useState(false);

  const loginWithSpotify = () => {
    window.location.href = "http://localhost:3001/login";
  };

  const joinSession = async () => {
    if (identifier) {
      setJoined(true);
    }
  };

  const addSong = async () => {
    if (!identifier || !songUri) return;
    try {
      await axios.post("http://localhost:3001/add-song", { identifier, songUri });
      alert("Canción añadida a la cola");
    } catch (error) {
      alert("Error al añadir la canción");
    }
  };

  return (
    <div className="flex flex-col items-center justify-center h-screen bg-gray-900 text-white">
      <h1 className="text-3xl font-bold mb-6">Jukebox Virtual</h1>
      <button
        onClick={loginWithSpotify}
        className="bg-green-500 px-4 py-2 rounded-lg mb-4"
      >
        Iniciar sesión con Spotify
      </button>
      <div className="flex flex-col items-center">
        <input
          type="text"
          placeholder="Código identificador"
          value={identifier}
          onChange={(e) => setIdentifier(e.target.value)}
          className="text-black px-2 py-1 rounded mb-2"
        />
        <button
          onClick={joinSession}
          className="bg-blue-500 px-4 py-2 rounded-lg mb-4"
        >
          Unirse a una sesión
        </button>
      </div>
      {joined && (
        <div className="flex flex-col items-center">
          <input
            type="text"
            placeholder="Spotify URI"
            value={songUri}
            onChange={(e) => setSongUri(e.target.value)}
            className="text-black px-2 py-1 rounded mb-2"
          />
          <button
            onClick={addSong}
            className="bg-red-500 px-4 py-2 rounded-lg"
          >
            Añadir canción
          </button>
        </div>
      )}
    </div>
  );
}

export default App;
